import React from "react";
import Pokemon from './Pokemon';



class App extends React.Component {
    state = {
        allPokemon : [],
        fetched : false,
        loading : false,
      };

      componentWillMount(){
        this.setState({
          loading : true
        });
        fetch('http://pokeapi.co/api/v2/pokemon?limit=400').then(res=>res.json())
        .then(response=>{
          this.setState({
            allPokemon : response.results,
            loading : true,
            fetched : true
          });
        });
      }
