    render(){
        const {fetched, loading, allPokemon} = this.state;
        let content ;
        if(fetched){
          content = <div className="pokemon--species--list">
              {allPokemon.map((pokemon,index)=><Pokemon key={pokemon.name} id={index+1} pokemon={pokemon}/>)}</div>;
        }else if(loading && !fetched){
            content = <p> Loading ...</p>;
        }
        else{
          content = <div/>;
        }
        return  <div>
          {content}
        </div>;
      }
}

export default App;
